(8, 'BỘ SGK TIẾNG ANH GLOBAL SUCCESS', 'bo-sgk-tieng-anh-global-success-8', NULL, 'http://tienganh.sachmem.vn/gioi-thieu/bo-sgk-tieng-anh-global-success', 4),
