INSERT INTO `book` (`id`, `name`, `slug`, `img`, `type`, `class`, `author`, `publishing`, `year`, `description`, `link`, `part`, `linksachmengv`, `linksachmemhs`, `linkgv`, `linksachgv2`, `linkudnghenoi`, `linkdekiemtra`, `linkstoryland`, `linkppct`, `linkudluyentuvung`, `linkudluyennghenoi`, `linkhoverimg`, `sachmem`, `idcate`) VALUES
(232, 'Nhà Khoa Học Trẻ Cấp độ 1 - Tập 5: Cây hồng gai', 'nha-khoa-hoc-tre-cap-do-1-tap-5-cay-hong-gai-232', '1626406427-capdo1tap51.jpg', 1, 1, 'Leong Sheng Ying, Tan Bee Lim, Chong Lap Hui, Yap Ming Yan', NULL, '2020', '<p>Bộ s&aacute;ch c&oacute; t&ecirc;n gốc l&agrave; &ldquo;The Young Scientists&rdquo;, được bi&ecirc;n soạn theo chương tr&igrave;nh gi&aacute;o dục khoa học của Singapore, được xuất bản tại Singapore từ năm 2000 v&agrave; vẫn đang xuất bản hằng th&aacute;ng. Ngay khi ra mắt, bộ s&aacute;ch đ&atilde; trở th&agrave;nh hiện tượng xuất bản tại Singapore, sớm được dịch v&agrave; xuất bản tại nhiều nước v&agrave; v&ugrave;ng l&atilde;nh thổ ch&acirc;u &Aacute;, như: Nhật Bản, H&agrave;n Quốc, Trung Quốc đại lục, Hồng K&ocirc;ng, Đ&agrave;i Loan, Phillipines, Malaysia. Việt Nam l&agrave; nước thứ 9 xuất bản bộ s&aacute;ch n&agrave;y dưới dạng một phi&ecirc;n bản đặc biệt, được in song ngữ Anh &ndash; Việt.</p>\r\n\r\n<p>Bộ s&aacute;ch được chia th&agrave;nh 4 cấp độ, mỗi cuốn tương ứng với một cấp độ, đ&atilde; được chỉnh sửa cho ph&ugrave; hợp với lứa tuổi học sinh tại Việt Nam.&nbsp;</p>\r\n\r\n<p>- Cấp độ 1: từ 6-8 tuổi;&nbsp;</p>\r\n\r\n<p>- Cấp độ 2: từ 9-10 tuổi;</p>\r\n\r\n<p>- Cấp độ 3: từ 11-12 tuổi;</p>\r\n\r\n<p>- Cấp độ 4: từ 13-14 tuổi.</p>\r\n\r\n<p>&nbsp;Nội dung chung của bộ s&aacute;ch l&agrave; phổ biến c&aacute;c kiến thức về khoa học thường thức như tr&aacute;i đất, vật l&yacute;, h&oacute;a học, m&ocirc;i trường, động thực vật học&hellip;, phổ biến c&aacute;c c&ocirc;ng nghệ mới, trang thiết bị hiện đại hiện nay tr&ecirc;n thế giới&hellip; nhưng dưới h&igrave;nh thức truyện tranh n&ecirc;n rất hấp dẫn, mềm mại, kh&ocirc;ng bị nặng về kiến thức chuy&ecirc;n ng&agrave;nh. Kh&ocirc;ng chỉ đọc để biết c&aacute;c kiến thức khoa học, c&ocirc;ng nghệ, s&aacute;ch c&ograve;n được tr&igrave;nh b&agrave;y dưới dạng song ngữ gi&uacute;p trẻ c&oacute; thể đọc trực tiếp bằng tiếng Anh để học từ vựng về c&aacute;c lĩnh vực khoa học.</p>\r\n\r\n<p><strong>Nội dung của cấp độ 1, tập 5:</strong></p>\r\n\r\n<ul>\r\n	<li>Linh cẩu - những t&ecirc;n cướp giật</li>\r\n	<li>Nấm kh&ocirc;ng c&oacute; hạt</li>\r\n	<li>Xe lăn c&oacute; thể leo cầu thang</li>\r\n	<li>Nếu kh&ocirc;ng c&oacute; cảm gi&aacute;c đau</li>\r\n	<li>Những sự thật về c&ocirc;n tr&ugrave;ng</li>\r\n	<li>Thức ăn của động vật</li>\r\n</ul>\r\n', '#', 0, '', '', '', '', '', '', '', '', '', '', '', 0, 2),
(233, 'Nhà Khoa Học Trẻ Cấp độ 1 - Tập 5: Cây hồng gai', 'nha-khoa-hoc-tre-cap-do-1-tap-5-cay-hong-gai-233', '1626406460-capdo1tap51.jpg', 1, 2, 'Leong Sheng Ying, Tan Bee Lim, Chong Lap Hui, Yap Ming Yan', NULL, '2020', '<p>Bộ s&aacute;ch c&oacute; t&ecirc;n gốc l&agrave; &ldquo;The Young Scientists&rdquo;, được bi&ecirc;n soạn theo chương tr&igrave;nh gi&aacute;o dục khoa học của Singapore, được xuất bản tại Singapore từ năm 2000 v&agrave; vẫn đang xuất bản hằng th&aacute;ng. Ngay khi ra mắt, bộ s&aacute;ch đ&atilde; trở th&agrave;nh hiện tượng xuất bản tại Singapore, sớm được dịch v&agrave; xuất bản tại nhiều nước v&agrave; v&ugrave;ng l&atilde;nh thổ ch&acirc;u &Aacute;, như: Nhật Bản, H&agrave;n Quốc, Trung Quốc đại lục, Hồng K&ocirc;ng, Đ&agrave;i Loan, Phillipines, Malaysia. Việt Nam l&agrave; nước thứ 9 xuất bản bộ s&aacute;ch n&agrave;y dưới dạng một phi&ecirc;n bản đặc biệt, được in song ngữ Anh &ndash; Việt.</p>\r\n\r\n<p>Bộ s&aacute;ch được chia th&agrave;nh 4 cấp độ, mỗi cuốn tương ứng với một cấp độ, đ&atilde; được chỉnh sửa cho ph&ugrave; hợp với lứa tuổi học sinh tại Việt Nam.&nbsp;</p>\r\n\r\n<p>- Cấp độ 1: từ 6-8 tuổi;&nbsp;</p>\r\n\r\n<p>- Cấp độ 2: từ 9-10 tuổi;</p>\r\n\r\n<p>- Cấp độ 3: từ 11-12 tuổi;</p>\r\n\r\n<p>- Cấp độ 4: từ 13-14 tuổi.</p>\r\n\r\n<p>&nbsp;Nội dung chung của bộ s&aacute;ch l&agrave; phổ biến c&aacute;c kiến thức về khoa học thường thức như tr&aacute;i đất, vật l&yacute;, h&oacute;a học, m&ocirc;i trường, động thực vật học&hellip;, phổ biến c&aacute;c c&ocirc;ng nghệ mới, trang thiết bị hiện đại hiện nay tr&ecirc;n thế giới&hellip; nhưng dưới h&igrave;nh thức truyện tranh n&ecirc;n rất hấp dẫn, mềm mại, kh&ocirc;ng bị nặng về kiến thức chuy&ecirc;n ng&agrave;nh. Kh&ocirc;ng chỉ đọc để biết c&aacute;c kiến thức khoa học, c&ocirc;ng nghệ, s&aacute;ch c&ograve;n được tr&igrave;nh b&agrave;y dưới dạng song ngữ gi&uacute;p trẻ c&oacute; thể đọc trực tiếp bằng tiếng Anh để học từ vựng về c&aacute;c lĩnh vực khoa học.</p>\r\n\r\n<p><strong>Nội dung của cấp độ 1, tập 5:</strong></p>\r\n\r\n<ul>\r\n	<li>Linh cẩu - những t&ecirc;n cướp giật</li>\r\n	<li>Nấm kh&ocirc;ng c&oacute; hạt</li>\r\n	<li>Xe lăn c&oacute; thể leo cầu thang</li>\r\n	<li>Nếu kh&ocirc;ng c&oacute; cảm gi&aacute;c đau</li>\r\n	<li>Những sự thật về c&ocirc;n tr&ugrave;ng</li>\r\n	<li>Thức ăn của động vật</li>\r\n</ul>\r\n', '#', 0, '', '', '', '', '', '', '', '', '', '', '', 0, 2),
